import { Router } from 'express';
const app = Router();
import Validate from '../helpers/validate'

import TransactionsController from '../controllers/transactions';
import AccountsController from '../controllers/accounts';
import UsersControllers from '../controllers/users';
import ActionControllers from '../controllers/actions'

app.get("/", (req, res) => { res.send("Welcome to papel") });
app.get('/transactions', Validate.verifyToken, TransactionsController.getAll);
app.get('/transactions/:transactionid', Validate.verifyToken, TransactionsController.getId)
app.get('/accounts', Validate.verifyToken, AccountsController.getAllAccounts)
app.get('/accounts/:accountnumber/transactions', Validate.verifyToken, AccountsController.getAccountTransactions)
app.get('/accounts/:accountnumber', Validate.verifyToken, AccountsController.getAccountDetails)
app.get('/user/:email/accounts', Validate.verifyToken, UsersControllers.getUserAccounts)
app.post('/auth/signup', ActionControllers.signUp)
app.post('/accounts/create', Validate.verifyToken, ActionControllers.createBankAccount)
app.post('/auth/signin', ActionControllers.signIn)
app.patch('/accounts/:accountnumber', Validate.verifyToken, ActionControllers.setNewStatus)
app.patch('/users/:type', Validate.verifyToken, ActionControllers.setPriviledge)
app.delete('/accounts/:accountnumber', Validate.verifyToken, ActionControllers.deleteAccount)
app.post('/transactions/:accountnumber/debit', Validate.verifyToken, TransactionsController.debit)
app.post('/transactions/:accountnumber/credit', Validate.verifyToken, TransactionsController.credit)
app.get('*', (req, res) => { res.send('The Page you are trying to access does not exist') })
app.post('*', (req, res) => { res.send('The Page you are trying to access does not exist') })
app.delete('*', (req, res) => { res.send('The Page you are trying to access does not exist') })

export default app;

import jwt from 'jsonwebtoken';
import connect from '../middleware/connect';

class Validate {

    static async verifyToken(req, res, next) {
        const token = req.get('x-access-token')
        if (!token) {
            return res.status(400).send({ 'message': 'Please provide a token' });
        }
        try {
            const decoded = jwt.decode(token, { complete: true });
            const userid = decoded.payload.userId
            const query = 'SELECT * FROM users WHERE userid = $1';
            const data = [
                userid
            ]
            const { rows } = await connect.query(query, data);
            if (!rows[0]) {
                return res.status(400).send({ 'message': 'This token does not exist' });
            }
            next();
        } catch (error) {
            return res.status(400).send(error);
        }
    }
}

export default Validate;

import { Pool } from 'pg';
import dotenv from 'dotenv';

dotenv.config();

const pool = new Pool({
    connectionString: process.env.DATABASE_URL,
    ssl: true,
    rejectUnauthorized: false,
});

export default {

    query(text, params) {
        return new Promise((resolve, reject) => {
            pool.query(text, params)
                .then((res) => {
                    resolve(res);
                })
                .catch((err) => {
                    reject(err);

                })
        })
    }
}

import connect from '../middleware/connect';

class TransactionsController {

    static async debit(req, res) {

        let query = 'SELECT newbalance FROM transactions where t_accountnumber = $1 ORDER BY createdon LIMIT 1'
        let data = [req.body.accountnumber]

        let { rows } = await connect.query(query, data)
        console.log(rows[0])

        query = `INSERT INTO 
        transactions (createdon, cashier, t_accountnumber, transactiontype, amount, oldbalance, newbalance)
        VALUES($1, $2, $3, $4, $5, $6, $7)
        returning *`;

        let balance = Number(rows[0]) - Number(req.body.amount)

        const values = [
            new Date(),
            req.body.cashier,
            req.params.accountnumber,
            'debit',
            req.body.amount,
            rows.newbalance,
            balance
        ];

        try {
            const { rows } = await connect.query(query, values);
            return res.status(201).send(rows[0]);
        } catch (error) {
            return res.status(400).send(error);
        }
    }

    static async credit(req, res) {
        let query = 'SELECT newbalance FROM transactions where t_accountnumber = $1 ORDER BY createdon LIMIT 1'
        let data = [req.body.accountnumber]

        let { rows } = await connect.query(query, data)
        console.log(rows[0])

        query = `INSERT INTO 
        transactions (createdon, cashier, t_accountnumber, transactiontype, amount, oldbalance, newbalance)
        VALUES($1, $2, $3, $4, $5, $6, $7)
        returning *`;

        let balance = Number(rows[0]) - Number(req.body.amount)

        const values = [
            new Date(),
            req.body.cashier,
            req.params.accountnumber,
            'debit',
            req.body.amount,
            rows.newbalance,
            balance
        ];

        try {
            const { rows } = await connect.query(query, values);
            return res.status(201).send(rows[0]);
        } catch (error) {
            return res.status(400).send('The action could not be completed');
        }
    }

    static async getAll(req, res) {
        const query = 'SELECT * FROM transactions';
        try {
            const { rows, rowCount } = await connect.query(query);
            return res.status(200).send({ rows, rowCount });
        } catch (error) {
            return res.status(400).send('The action could not be completed');
        }
    }

    static async getId(req, res) {
        const query = 'SELECT * FROM transactions WHERE id = $1';
        try {
            const { rows } = await connect.query(query, [req.params.id]);
            if (!rows[0]) {
                return res.status(404).send({ 'message': 'transaction not found' });
            }
            return res.status(200).send(rows[0]);
        } catch (error) {
            return res.status(400).send('The action could not be completed')
        }
    }

}

export default TransactionsController;

import connect from '../middleware/connect';

class AccountsController {

    static async getAllAccounts(req, res) {

        let query;
        let values;

        if (!req.query.status) {
            query = 'SELECT * FROM accounts'
            values = []
        } else {
            query = "SELECT * FROM accounts where status like $1"
            values = [
                req.query.status
            ]
        }

        try {
            const { rows, rowCount } = await connect.query(query, values);
            return res.status(200).send({ rows, rowCount });
        } catch (error) {
            return res.status(400).send('The action could not be completed');
        }
    }

    static async getAccountTransactions(req, res) {
        const query = 'select * from transactions where t_accountnumber = $1';
        try {
            const { rows } = await connect.query(query, [req.params.accountnumber]);
            if (!rows[0]) {
                return res.status(404).send({ 'message': 'no transactions found' });
            }
            return res.status(200).send(rows[0]);
        } catch (error) {
            return res.status(400).send('The action could not be completed')
        }
    }

    static async getAccountDetails(req, res) {
        const query = 'select a.createdon, a.accountnumber, u.email, a.a_type, a.status, a.balance from accounts a, users u where a.owner=u.u_id and a.accountnumber = $1'
        try {
            const { rows } = await connect.query(query, [req.params.accountnumber]);
            if (!rows[0]) {
                return res.status(404).send({ 'message': 'no Account Details found' });
            }
            return res.status(200).send(rows[0]);
        } catch (error) {
            return res.status(400).send('The action could not be completed')
        }
    }

}

